import React, { useState } from 'react';
import { StyleSheet, View } from 'react-native';
import { Input, Button, Icon } from 'react-native-elements';
import { validateEmail } from '../../utils/validations';
import Loading from '../Loading'
import { size, isEmpty } from 'lodash';
import * as firebase from 'firebase';
import {useNavigation} from '@react-navigation/native'


function defaultFormValue() {
	return {
		email: '',
		password: '',
		repeatPassword: ''
	};
}
/**
 * Formulario de registro 
 */
function RegisterForm(props) {
	const { toastRef } = props;
	const [ showPassword, setShowPassword ] = useState(false);
	const [ showRepitPassword, setshowRepitPassword ] = useState(false);
	const [ formData, setformData ] = useState(defaultFormValue());
	const [loading, setloading] = useState(false)
	const navigation = useNavigation()
	const onSubmit = () => {
		//console.log(formData);
		var { email, password, repeatPassword } = formData;
		if (isEmpty(email) || isEmpty(password) || isEmpty(repeatPassword)) {
			console.log('Todos los campos son obligatorios');
			toastRef.current.show('Todos los campos son obligatorios');
		} else {
			if (!validateEmail(email)) {
				console.log('Debe ser un correo valido');
				toastRef.current.show('Debe ser un correo valido');
			} else if (size(password) <= 6 || size(repeatPassword) <= 6) {
				console.log('la contrase単a deberia tener por lo menos tantos caracteres');
				toastRef.current.show('la contrase単a deberia tener por lo menos tantos caracteres');
			} else if (password !== repeatPassword) {
				console.log('las contrase単as deberian ser iguales');
				toastRef.current.show('las contrase単as deberian ser iguales');
			} else {
				//console.log('ok');
				setloading(true)
				firebase
					.auth()
					.createUserWithEmailAndPassword(email, password)
					.then((response) => {
						//console.log(response);
						setloading(false)
						navigation.navigate('Profile')
					})
					.catch((error) => {
						//console.log(error);
						toastRef.current.show('Ya hay un usuario con este mismo email')
						setloading(true)
					});
			}
		}
	};

	const onChange = (even, type) => {
		setformData({
			...formData,
			[type]: even.nativeEvent.text
		});
	};
	return (
		<View style={styles.formContainer}>
			<Input
				label="Correo electr坦nico"
				placeholder="Correo Electr坦nico"
				onChange={(even) => onChange(even, 'email')}
				containerStyle={styles.inputForm}
				rightIcon={
					<Icon name="envelope" type="font-awesome" size={17} color="gray" iconStyle={styles.iconRight} />
				}
			/>
			<Input
				label="Contrase単a"
				placeholder="Contrase単a"
				containerStyle={styles.inputForm}
				password={true}
				onChange={(even) => onChange(even, 'password')}
				secureTextEntry={showPassword ? false : true}
				rightIcon={
					<Icon
						name={showPassword ? 'eye-outline' : 'eye-off-outline'}
						type="material-community"
						size={20}
						color="gray"
						iconStyle={styles.iconRight}
						onPress={() => {
							setShowPassword(!showPassword);
						}}
					/>
				}
			/>
			<Input
				label="Repetir Contrase単a"
				placeholder="Repetir Contrase単a"
				containerStyle={styles.inputForm}
				password={true}
				onChange={(even) => onChange(even, 'repeatPassword')}
				secureTextEntry={showRepitPassword ? false : true}
				rightIcon={
					<Icon
						name={showRepitPassword ? 'eye-outline' : 'eye-off-outline'}
						type="material-community"
						size={20}
						color="gray"
						iconStyle={styles.iconRight}
						onPress={() => {
							setshowRepitPassword(!showRepitPassword);
						}}
					/>
				}
			/>
			<Button
				title="Registrarse"
				containerStyle={styles.btnContainerRegister}
				buttonStyle={styles.btnRegister}
				onPress={onSubmit}
			/>

			<Loading isVisible={loading} text="Creando Cuenta" />
		</View>
	);
}

export default RegisterForm;

const styles = StyleSheet.create({
	formContainer: {
		flex: 1,
		alignItems: 'center',
		justifyContent: 'center',
		marginTop: 30
	},
	iconRight: {},
	inputForm: {
		width: '100%',
		marginTop: 20
	},
	btnContainerRegister: {
		marginTop: 20,
		width: '90%',
		alignItems: 'center'
	},
	btnRegister: {
		backgroundColor: '#1A89E7'
	}
});
